mapper.insert:
INSERT INTO 
	cfmyitem (
	myItemName
	, myItemAmount
	, myItemExpiration
	)
VALUES (
	#{myItemName}
	, #{myItemAmount}
	, #{myItemExpiration}
	)

mapper.update:

	UPDATE cfmyitem
	SET 
		myItemName = #{myItemName}
		, myItemAmount = #{myItemAmount}
		, myItemExpiration = #{myItemExpiration}
	WHERE
		myItemCode = #{myItemCode}
