mapper.update:
UPDATE cffinance 
SET 
	comSystemNumeral = #{comSystemNumeral}
	, memberEmail = #{memberEmail}
	, financeAmount = #{financeAmount}
	, closingStatementCode = #{closingStatementCode}
WHERE financeCode = #{financeCode}

mapper.select:
SELECT c.comSystemName , f.financeCode , f.comCode , f.comSystemNumeral , f.memberEmail , f.financeAmount , f.closingStatementCode FROM cffinance AS f INNER JOIN cfcomsystem AS c ON f.comSystemNumeral = c.comSystemNumeral WHERE f.comCode = #{comCode}
AND f.financeCode = #{financeCode}
AND f.closingStatementCode LIKE CONCAT('%', #{closingStatementCode}, '%')

mapper.insert:
INSERT INTO cffinance (
	comCode
	, comSystemNumeral
	, memberEmail
	, financeAmount
	, closingStatementCode
) VALUES (
	#{comCode}
	, #{comSystemNumeral}
	, #{memberEmail}
	, #{financeAmount}
	, #{closingStatementCode}
)
